import './App.css';
import { useState } from 'react';





function App() {

    return (
        <div className="App">
            <SignOut />
            Vini Bezerra
            <hr />
            <PartySelector />
            <hr />
            <Party />
        </div>
    );
}

function SignIn() {

    return (
        <>
            <button className="sign-in">Sign in with Google</button>
        </>
    )
}

function SignOut() {
    return (
        <button className="sign-out">Sign Out</button>
    )
}

function Party() {
    const [guestList, setGuestList] = useState(initialGuests);

    function handleAddGuest(name: string) {
        setGuestList([
            ...guestList,
            { id: nextIndex, name: name, status: "cadastrado" }
        ])
        nextIndex++
    }

    return (

        <>
            <GuestInput onEnter={handleAddGuest}
            />
            <GuestList list={guestList} setList={setGuestList} />
        </>
    )
}

function PartySelector() {
    return (
        <Dropdown>

        </Dropdown>
    )
}

function GuestInput({ onEnter }) {
    const [text, setText] = useState("");

    function handleKeyUp(event) {
        if (event.key === "Enter") {
            if (text) { onEnter(text) }
            setText("")
        }
    }

    return (
        <input
            value={text}
            onChange={e => setText(e.target.value)}
            onKeyUp={handleKeyUp}
        />

    )
}

function Guest({ guest, onDelete, onStatusButton }) {
    let disable = false
    let lable = "convidar"


    switch (guest.status) {
        case "cadastrado": {
            lable = 'convidar'
            break
        }
        case "convidado": {
            lable = 'confirmar'
            break
        }
        case "confirmado": {
            lable = '✅'
            disable = true
            break
        }
        default: { }
    }

    return (
        <li>
            <button className="btndanger" onClick={() => onDelete(guest.id)}>X</button>
            <button
                onClick={() => onStatusButton(guest.id)}
                disabled={disable}>
                {lable}
            </button>
            {guest.name}
        </li>
    )
}

function GuestList({ list, setList }) {
    function handleDelete(id) {
        setList(list.filter(g => (g.id !== id)))
    }
    function handleStatusButton(id) {
        setList(list.map(g => {
            if (g.id === id) {
                let newStatus = "cadastrado"
                if (g.status === "cadastrado") {
                    newStatus = "convidado"
                } else if (g.status === "convidado") {
                    newStatus = "confirmado"
                }
                return {
                    ...g,
                    status: newStatus
                }
            } else {
                return g
            }
        }))
    }

    return (
        <>
            <ul>
                {list.map(guest => {
                    return (
                        <Guest
                            guest={guest}
                            onDelete={handleDelete}
                            onStatusButton={handleStatusButton}
                        />
                    )
                })}
            </ul>
        </>
    )
}

let nextIndex = 4;
const initialGuests = [
    { id: 1, name: "vini", status: "confirmado" },
    { id: 2, name: "amaral", status: "cadastrado" },
    { id: 3, name: "yasmin", status: "convidado" },
]


export default App;
