import React, { useState } from 'react';
import Modal from 'react-modal';
import './Modals.css';
import './App.css';

const CorpoB = () => {
    const [modalIsOpen, setModalIsOpen] = useState(false);

    const openModal = () => {
        setModalIsOpen(true);
    };

    const closeModal = () => {
        setModalIsOpen(false);
    };

    return (
        <div className="imageBallonBox">
            <img
                className="imageBallon"
                src='.\botaoAdicionarFesta.PNG'
                alt="BalÃ£o de festa"
                onClick={openModal}
            />
            <ModalShowFest
                isOpen={modalIsOpen}
                onRequestClose={closeModal}
            />
        </div>
    );
};

interface ModalShowFestProps {
    isOpen: boolean;
    onRequestClose: () => void;
}

const ModalShowFest: React.FC<ModalShowFestProps> = ({ isOpen, onRequestClose }) => {
    const [modalIsOpenPessoa, setModalIsOpenPessoa] = useState(false);

    const openModalPessoa = () => {
        setModalIsOpenPessoa(true);
    };

    const closeModalPessoa = () => {
        setModalIsOpenPessoa(false);
    };

    return (
        <Modal
            isOpen={isOpen}
            onRequestClose={onRequestClose}
            contentLabel="Exemplo Modal"
        >
            <h3 className='modal-titulo'>Participantes</h3>
            <div className="uk-flex uk-flex-row uk-flex-between" id="navbar-modal">
                <div className="uk-navbar">
                    <span className="uk-navbar-toggle-icon"></span>
                    <span className="uk-margin-small-left">Pessoa</span>
                </div>

                <div className="uk-navbar">
                    <span className="uk-navbar-toggle-icon"></span>
                    <span className="uk-margin-small-left">Convidado</span>
                </div>

                <div className="uk-navbar">
                    <span className="uk-navbar-toggle-icon"></span>
                    <span className="uk-margin-small-left">Confirmado</span>
                </div>
            </div>

            <button className="modal-button" onClick={openModalPessoa}>
                Adicionar Pessoa
            </button>
        </Modal>
    );
};

{/* banco de dados*/ }
let nextIndex = 4;
const initialGuests = [
    { id: 1, name: "vini", status: "confirmado" },
    { id: 2, name: "amaral", status: "cadastrado" },
    { id: 3, name: "yasmin", status: "convidado" },
]

export default CorpoB;
