import React, { useState } from 'react';
import Modal from 'react-modal';
import './Modals.css';
import './App.css';

const CorpoB: React.FC = () => {
    const [modalIsOpen, setModalIsOpen] = useState(false);

    const openModal = () => {
        setModalIsOpen(true);
    };

    const closeModal = () => {
        setModalIsOpen(false);
    };

    return (
        <div className="imageBallonBox">
            <img
                className="imageBallon"
                src='.\botaoAdicionarFesta.PNG'
                alt="BalÃ£o de festa"
                onClick={openModal}
            />
            <ModalShowFest
                isOpen={modalIsOpen}
                onRequestClose={closeModal}
            />
        </div>
    );
};

interface ModalShowFestProps {
    isOpen: boolean;
    onRequestClose: () => void;
}

const ModalShowFest: React.FC<ModalShowFestProps> = ({ isOpen, onRequestClose }) => {
    const [modalIsOpenPessoa, setModalIsOpenPessoa] = useState(false);

    const openModalPessoa = () => {
        setModalIsOpenPessoa(true);
    };

    const closeModalPessoa = () => {
        setModalIsOpenPessoa(false);
    };

    interface GuestProps {
        guest: { id: number; name: string; status: string };
        onDelete: (id: number) => void;
        onStatusButton: (id: number) => void;
    }

    const Guest: React.FC<GuestProps> = ({ guest, onDelete, onStatusButton }) => {
        let disable = false;
        let label = "convidar";
        let css = "convite";

        switch (guest.status) {
            case "cadastrado": {
                label = "convidar";
                break;
            }
            case "convidado": {
                label = 'confirmar';
                css = 'confirmei';
                break;
            }
            case "confirmado": {
                label = 'âœ…';
                disable = true;
                css = 'confirmei';
                break;
            }
            default: { }
        }

        return (
            <li>
                <button className="btndanger" onClick={() => onDelete(guest.id)}>X</button>
                <button
                    onClick={() => onStatusButton(guest.id)}
                    disabled={disable}
                    className={css === 'convite' ? 'convite' : css === 'confirme' ? 'confirme' : 'confirmei'}>
                    {label}
                </button>
                {guest.name}
            </li>
        );
    };

    return (
        <Modal
            isOpen={isOpen}
            onRequestClose={onRequestClose}
            contentLabel="Exemplo Modal"
        >
            <h3 className='modal-titulo'>Participantes</h3>
            <div className="uk-flex uk-flex-row uk-flex-between" id="navbar-modal">
                <div className="uk-navbar">
                    <span className="uk-navbar-toggle-icon"></span>
                    <span className="uk-margin-small-left">Pessoa</span>
                </div>

                <div className="uk-navbar">
                    <span className="uk-navbar-toggle-icon"></span>
                    <span className="uk-margin-small-left">Convidado</span>
                </div>

                <div className="uk-navbar">
                    <span className="uk-navbar-toggle-icon"></span>
                    <span className="uk-margin-small-left">Confirmado</span>
                </div>
            </div>


            <ul>
                <Guest
                    guest={{ id: 1, name: "Exemplo", status: "cadastrado" }}
                    onDelete={(id) => console.log(`Excluir convidado ${id}`)}
                    onStatusButton={(id) => console.log(`Alterar status do convidado ${id}`)}
                />

            </ul>

            <button className="modal-button" onClick={openModalPessoa}>
                Adicionar Pessoa
            </button>

            <ModalShowParticipants
                onEnter={(text) => {
                    console.log(text);
                }}
                isOpen={modalIsOpenPessoa}
                onRequestClose={closeModalPessoa}
            />
        </Modal>
    );
};

interface ModalShowParticipantsProps {
    onEnter: (text: string) => void;
    isOpen: boolean;
    onRequestClose: () => void;
}

const ModalShowParticipants: React.FC<ModalShowParticipantsProps> = ({ onEnter, isOpen, onRequestClose }) => {
    const [text, setText] = useState("");

    function handleKeyUp(event: React.KeyboardEvent<HTMLInputElement>) {
        if (event.key === "Enter") {
            if (text) {
                onEnter(text);
            }
            setText("");
        }
    }

    return (
        <Modal
            isOpen={isOpen}
            onRequestClose={onRequestClose}
            contentLabel="Mostrar Participantes Modal"
        >
            <div className="uk-flex uk-flex-row uk-flex-between" id="navbar-modalP">
                <div className="uk-navbar">
                    <span className="uk-navbar-toggle-icon"></span>
                    <span className="uk-margin-small-left">Adicionar participantes</span>
                </div>

                <div className="uk-navbar" id="input">
                    <span className="uk-margin-small-left">
                        <input
                            value={text}
                            onChange={(e) => setText(e.target.value)}
                            onKeyUp={handleKeyUp}
                        />
                    </span>
                </div>
            </div>
        </Modal>
    );
};

let nextIndex = 4;
const initialGuests = [
    { id: 1, name: "vini", status: "confirmado" },
    { id: 2, name: "amaral", status: "cadastrado" },
    { id: 3, name: "yasmin", status: "convidado" },
]

export default CorpoB;
